import {css} from 'styled-components'
import slide from './slide'
import fade from './fade'

/**
 * A sliding fade transition.
 *
 * @public
 * @property {Array} in
 *   Fades in
 * @property {Array} in.normal
 *   Transition from left to right
 * @property {Array} in.reverse
 *   Transition from right to left
 * @property {Array} in.up
 *   Transition upwards
 * @property {Array} in.down
 *   Transition downwards
 * @property {Array} out
 *   Fades out
 * @property {Array} out.normal
 *   Transition from left to right
 * @property {Array} out.reverse
 *   Transition from right to left
 * @property {Array} out.up
 *   Transition upwards
 * @property {Array} out.down
 *   Transition downwards
 */
const fadeSlide = {
  in: {
    normal: css`
      --direction: 1;
      --regulator: 0;
      ${fade.in};
      ${slide.normal};
    `,
    reverse: css`
      --direction: 1;
      --regulator: 0;
      ${fade.in};
      ${slide.reverse};
    `,
    up: css`
      ${fade.in};
      ${slide.up};
    `,
    down: css`
      ${fade.in};
      ${slide.down};
    `
  },
  out: {
    normal: css`
      --direction: 1;
      --regulator: 1;
      ${fade.out};
      ${slide.normal};
    `,
    reverse: css`
      --direction: 1;
      --regulator: 1;
      ${fade.out};
      ${slide.reverse};
    `,
    up: css`
      --direction: -1;
      --regulator: 1;
      ${fade.out};
      ${slide.up};
    `,
    down: css`
      --direction: -1;
      --regulator: 1;
      ${fade.out};
      ${slide.down};
    `
  }
}

export default fadeSlide

import {css} from 'styled-components'

/**
 * A simple flip transition.
 * @property {Array} x
 *   Flips on the x axis
 * @property {Array} y
 *   Flips on the y axis
 */
const flip = {
  x: css`
    backface-visibility: hidden;
    transform: perspective(200vw)
      rotate3d(0, 1, 0, calc(180deg * var(--direction, -1) * var(--time, 1)));
  `,
  y: css`
    backface-visibility: hidden;
    transform: perspective(200vh)
      rotate3d(1, 0, 0, calc(180deg * var(--direction, -1) * var(--time, 1)));
  `
}

export default flip

/* global window */
import {Component} from 'react'
import PropTypes from 'prop-types'
import dateHelpers from './date-helpers'

export {default as renderCountdown} from './render-countdown'

/**
 * A countdowntimer that can be paused.
 * @public
 * @param {Object} props
 *   The properties
 * @param {Boolean} props.isRunning
 *   Defines if the timer is running or paused
 * @param {number} props.timerWarning
 *   Defines when the timer warns you
 * @param {number} props.end
 *   Defines when the timerends
 * @param {renderCountdown} props.render
 *   A function that can be passed to render the timer
 */
export default class Countdown extends Component {
  /**
   * @private
   */
  static get propTypes() {
    return {
      isRunning: PropTypes.bool,
      timerWarning: PropTypes.number,
      end: PropTypes.number.isRequired,
      render: PropTypes.func.isRequired
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      isRunning: false,
      timerWarning: 0
    }
  }

  /**
   * A countdowntimer that can be paused.
   * @public
   * @param {Object} props
   *   The properties
   * @param {Boolean} props.isRunning
   *   Defines if the timer is running or paused
   * @param {number} props.timerWarning
   *   Defines when the timer warns you
   * @param {number} props.end
   *   Defines when the timerends
   * @param {renderCountdown} props.render
   *   A function that can be passed to render the timer
   */
  constructor(props) {
    super(props)

    /**
     * @private
     * @property {number} days
     * @property {number} hours
     * @property {number} minutes
     * @property {number} seconds
     * @property {number} start
     * @property {number} waiting
     * @property {number} waited
     */
    this.state = {
      days: 0,
      hours: 0,
      minutes: 0,
      seconds: 0,
      start: this.now.getTime(),
      waiting: this.props.isRunning ? 0 : this.now.getTime(),
      waited: 0
    }

    this.run = this.run.bind(this)
    this.stop = this.stop.bind(this)
  }

  /**
   * @private
   */
  get now() {
    return new Date()
  }

  /**
   * @private
   */
  get then() {
    return this.state.start + this.state.waited + this.props.end * dateHelpers.m
  }

  /**
   * @private
   * @param {(Boolean|number)} force
   *   If a `Boolean` use it to force runs.
   */
  run(force = false) {
    const now = this.now
    const then = this.then
    const diff = then - now
    const done = diff <= 0
    const days = done ? 0 : Math.abs(~~(diff / dateHelpers.d))
    const hours = done ? 0 : Math.abs(~~(diff / dateHelpers.h) % 24)
    const minutes = done ? 0 : Math.abs(~~(diff / dateHelpers.m) % 60)
    const seconds = done ? 0 : Math.abs(~~(diff / dateHelpers.s) % 60)
    if ((!done && this.props.isRunning) || (!done && force === true)) {
      this.setState({
        done,
        days,
        hours,
        minutes,
        seconds
      })
      window.requestAnimationFrame(this.run)
    } else {
      this.stop()
    }
  }

  /**
   * @private
   */
  stop() {
    window.cancelAnimationFrame(this.run)
  }

  /**
   * @private
   */
  componentWillMount() {
    const now = this.now.getTime()
    const then = this.state.waited + this.props.end * dateHelpers.m
    this.setState({
      start: now,
      days: Math.abs(~~(then / dateHelpers.d)),
      hours: Math.abs(~~(then / dateHelpers.h) % 24),
      minutes: Math.abs(~~(then / dateHelpers.m) % 60),
      seconds: Math.abs(~~(then / dateHelpers.s) % 60)
    })

    if (this.props.isRunning) {
      this.run()
    } else {
      this.setState({
        waiting: now
      })
    }
  }

  /**
   * @private
   * @param {Object} newProps
   */
  componentWillReceiveProps(newProps) {
    if (newProps.isRunning && !this.props.isRunning) {
      const {waiting} = this.state
      this.setState(prevState => ({
        waited: prevState.waited + (this.now.getTime() - waiting)
      }))
      this.run(true)
    } else if (!newProps.isRunning && this.props.isRunning) {
      this.setState({
        waiting: this.now.getTime()
      })
      this.stop()
    }
  }

  /**
   * @private
   */
  componentWillUnmount() {
    this.stop()
  }

  /**
   * @private
   */
  render() {
    const {days, hours, minutes, seconds, done} = this.state
    return this.props.render(
      {
        days,
        hours,
        minutes,
        seconds
      },
      done,
      this.props.timerWarning
    )
  }
}

/**
 * @typedef renderCountdown
 * @public
 * @param {Object} data
 *   Time data
 * @param {number} data.days
 *   Remaining days
 * @param {number} data.hours
 *   Remaining hours
 * @param {number} data.minutes
 *   Remaining minutes
 * @param {number} data.seconds
 *   Remaining seconds
 * @param {Boolean} isDone
 *   Triggered when the timer is done
 * @param {Boolean} timerWarning
 *   Triggered when the warning limit has been reached
 * @return {ReactElement}
 */

/* eslint-disable react/prop-types */
import React from 'react'

/**
 * Pads a number with a leading zero if it is lower than 10
 * @private
 * @param {number} n
 * @return {String}

 */
const leadingZero = n => (n < 10 ? `0${n}` : n)

/**
 * Renders a coundown in a simple div.
 * A data-attribute `data-warning` is added when the timerWarning is
 * active.
 *
 * @public
 * @param {Object} data
 *   Time data
 * @param {number} data.days
 *   Remaining days
 * @param {number} data.hours
 *   Remaining hours
 * @param {number} data.minutes
 *   Remaining minutes
 * @param {number} data.seconds
 *   Remaining seconds
 * @param {Boolean} isDone
 *   Triggered when the timer is done
 * @param {Boolean} timerWarning
 *   Triggered when the warning limit has been reached
 * @return {ReactElement}
 */
const renderSimpleCountdown = (
  {days, hours, minutes, seconds},
  isDone,
  timerWarning
) => {
  const props = {}
  if (!days && !hours && minutes * 60 + seconds < timerWarning) {
    props['data-warning'] = ''
  }
  return (
    <span {...props}>
      {leadingZero(hours)}:{leadingZero(minutes)}:{leadingZero(seconds)}
    </span>
  )
}

export default renderSimpleCountdown

import React from 'react'
import PropTypes from 'prop-types'
import styled, {css} from 'styled-components'
import Icon from '@dekk/icon'
import image from '@dekk/icon/lib/image'

/**
 * @private
 */
const Controls = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  box-sizing: border-box;
  grid-area: Controls;
  display: grid;
  grid-template-rows: 2rem 2rem auto;
  grid-template-columns: 10rem 1fr repeat(3, 2rem);
  grid-gap: 0.25rem;
  grid-template-areas:
    'PageNumber ControlB PlayButton ThemeToggle LayoutToggle '
    'FragmentNumber . . . .'
    'ControlA ControlA ControlA ControlA ControlA';
`
export default Controls

/**
 * @private
 */
const toggleStyle = css`
  background: ${({theme}) => (theme === 'dark' ? '#222' : '#ddd')};
  color: ${({theme}) => (theme === 'dark' ? '#ddd' : '#222')};
  box-shadow: 0 0 0 1px ${({theme}) => (theme === 'dark' ? '#333' : '#ccc')}
    inset;
  border: 0;
  border-radius: 3px;
  height: 2rem;
  width: 2rem;
  padding: 0.5rem;
  font-size: 1rem;
  cursor: pointer;

  &:hover {
    background: ${({theme}) => (theme === 'dark' ? '#333' : '#ccc')};
  }

  &:active {
    background: ${({theme}) => (theme === 'dark' ? '#444' : '#bbb')};
  }

  &:focus {
    outline: 0;
    background: highlight;
  }

  svg {
    height: 1em;
    width: 1em;
  }
`

/**
 * @private
 */
const StyledLayoutToggle = styled.button`
  grid-area: LayoutToggle;
  ${toggleStyle};
`

/**
 * @private
 */
export const LayoutToggle = props => (
  <StyledLayoutToggle {...props}>
    <Icon icon={image['view-compact']} />
  </StyledLayoutToggle>
)

/**
 * @private
 */
const StyledThemeToggle = styled.button`
  grid-area: ThemeToggle;
  ${toggleStyle};
`

/**
 * @private
 */
const StyledPlayButton = styled.button`
  grid-area: PlayButton;
  ${toggleStyle};
`

/**
 * @private
 */
export const ControlA = styled.div`
  grid-area: ControlA;
  position: relative;
  height: 100%;
  width: 100%;
  padding: 0.75rem 1rem 1rem;
  box-sizing: border-box;
`

/**
 * @private
 */
export const ControlB = styled.div`
  grid-area: ControlB;
  position: relative;
  height: 100%;
  width: 100%;
  box-sizing: border-box;
  padding: 0 0.75rem;
  display: flex;
  align-items: center;
  align-content: center;
  justify-content: flex-end;
`

/**
 * @private
 */
export const PageNumber = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  align-content: center;
  grid-area: PageNumber;
  box-shadow: 0 -1px 0 0 ${({theme}) => (theme === 'dark' ? '#222' : '#ddd')} inset;
`

/**
 * @private
 */
export const FragmentNumber = styled.div`
  position: relative;
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  align-content: center;
  grid-area: FragmentNumber;
  box-shadow: 0 -1px 0 0 ${({theme}) => (theme === 'dark' ? '#222' : '#ddd')} inset;
`

/**
 * @private
 */
export const Label = styled.span`
  display: inline-block;
  text-align: right;
  padding-right: 0.5rem;
  width: 6rem;
  min-width: max-content;
`

/**
 * @private
 */
export const Countdown = styled.div`
  font-size: 2rem;

  [data-warning] {
    color: #a00;
  }
`

/**
 * @private
 */
export const PlayButton = props => {
  const icon = props.isPlaying ? image['timer-off'] : image.timer
  return (
    <StyledPlayButton {...props}>
      <Icon icon={icon} />
    </StyledPlayButton>
  )
}

/**
 * @private
 */
export const ThemeToggle = props => {
  const icon = props.isDark ? image['wb-sunny'] : image['brightness-2']
  return (
    <StyledThemeToggle {...props}>
      <Icon icon={icon} />
    </StyledThemeToggle>
  )
}

/**
 * @private
 */
PlayButton.propTypes = {
  isPlaying: PropTypes.bool
}

/**
 * @private
 */
PlayButton.defaultProps = {
  isPlaying: false
}

/**
 * @private
 */
ThemeToggle.propTypes = {
  isDark: PropTypes.bool
}

/**
 * @private
 */
ThemeToggle.defaultProps = {
  isDark: false
}

import React, {Component, Children, cloneElement} from 'react'
import PropTypes from 'prop-types'
import {observer} from 'mobx-react'
import {range} from '@dekk/utils'
import Store from '@dekk/store'
import Wrapper from './wrapper'
import SlidesWrapper from './slides-wrapper'
import Plugins from './plugins'
import Elements from './elements'

/**
 * A wrapper around the slides.
 * `<Deck/>` renders 3 slides (previous, current, next) to allow various
 * transitions.
 *
 * The internal store is handled by mobX. {@link https://github.com/mobxjs/}
 *
 * @class Deck
 * @param {Object} props
 *   The properties
 * @param {(Slide|Slide[])} props.children
 * @param {?String} props.mixin
 *
 * @example
 * import React from 'react'
 * import Deck from '@dekk/deck'
 * import Slide from '@dekk/slide'
 *
 * const App = () => (
 *   <Dekk>
 *     <Slide>1</Slide>
 *     <Slide>2</Slide>
 *     <Slide>3</Slide>
 *   </Dekk>
 * )
 */
@observer
export default class Deck extends Component {
  /**
   * Store.
   * It handles paging and fragmentOrder navigation
   * @private
   */
  store = new Store()

  /**
   * @private
   * @return {{children: (Slide|Slide[]), mixin: ?String, slave: ?Boolean}}
   *   Allowed propTypes for `<Deck/>`
   */
  static get propTypes() {
    return {
      children: PropTypes.node.isRequired,
      mixin: PropTypes.oneOfType([PropTypes.array, PropTypes.string])
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      mixin: ''
    }
  }

  /**
   * Sends context to decendants.
   * Includes the app store to allow all deck components to access the store
   * without having to include its own logic.
   * @private
   * @return {{store: Object, fragmentHost: number}}
   *   The `store` and the `fragmentHost`
   */
  static get childContextTypes() {
    return {
      store: PropTypes.object.isRequired,
      fragmentHost: PropTypes.number
    }
  }

  /**
   * @private
   * @return {{store: store}}
   *   The child context.
   */
  getChildContext() {
    return {
      store: this.store
    }
  }

  /**
   * @private
   */
  componentWillMount() {
    this.buildFragmentHosts()
  }

  /**
   * @private
   */
  componentWillReceiveProps(newProps) {
    // Rebuild fragmentHost when children change
    // this will prevent undefined hosts while reducing the number
    // of calls
    if (newProps.children !== this.props.children) {
      this.buildFragmentHosts()
    }
  }

  /**
   * @private
   */
  buildFragmentHosts() {
    this.slides.forEach((child, index) => {
      if (this.store.fragmentHosts.length - 1 < index)
        this.store.fragmentHosts[index] = []
    })
  }

  /**
   * @private
   */
  get plugins() {
    const {slideIndex, fragmentIndex, fragmentOrder} = this.store
    const {length: slideCount} = this.slides
    const {length: fragmentCount} = this.store.fragmentHosts[slideIndex]
    return Children.toArray(this.props.children)
      .filter(child => typeof child === 'object' && child.type === Plugins)
      .reduce((a, b) => a.concat(b.props.children), [])
      .filter(Boolean)
      .map((plugin, index) =>
        cloneElement(plugin, {
          key: `${plugin.type.name}_${index}`,
          ...this.store.publicMethods,
          slideIndex,
          slideCount,
          fragmentIndex,
          fragmentCount,
          fragmentOrder
        })
      )
  }

  /**
   * @private
   */
  get elements() {
    const {slideIndex, fragmentIndex, fragmentOrder} = this.store
    const {length: slideCount} = this.slides
    const {length: fragmentCount} = this.store.fragmentHosts[slideIndex]
    return Children.toArray(this.props.children)
      .filter(child => typeof child === 'object' && child.type === Elements)
      .reduce((a, b) => a.concat(b.props.children), [])
      .filter(Boolean)
      .map((element, index) =>
        cloneElement(element, {
          key: `${element.type.name}_${index}`,
          ...this.store.publicMethods,
          slideIndex,
          slideCount,
          fragmentIndex,
          fragmentCount,
          fragmentOrder
        })
      )
  }

  /**
   * @private
   */
  get slots() {
    return [Plugins, Elements]
  }

  /**
   * @private
   */
  get slides() {
    return Children.toArray(this.props.children).filter(
      child => !this.slots.includes(child.type)
    )
  }

  /**
   * Get the `children` by a range of `+-1` around the current slide.
   * It renders a maximum of 3 slides (previous, current, next)
   * This allows various transitions between slides.
   *
   * @private
   * @return {Array<Slide>} returns an array of max 3 slides
   */
  get visibleSlides() {
    const {slideIndex, direction, fragmentOrder, fragmentHosts} = this.store
    return (
      this.slides
        // Assign the original index for the Component
        .map((child, originalIndex) => ({child, originalIndex}))
        // Filter by a range of `+-1`
        // Filter first to reduce the number of clones
        // This will not allow us to get all fragments on init but
        // improves performance
        .filter((c, i) => range(i, slideIndex + 1, slideIndex - 1))
        // Modify the remaining slides (maximum of 3)
        .map(({child, originalIndex}) => {
          const {length: fragmentCount = 0} = fragmentHosts[originalIndex]
          const lastFragment = Math.max(0, fragmentCount - 1)
          // Flags to check for value
          const isCurrent = slideIndex === originalIndex
          const isPrev = slideIndex === originalIndex + 1
          const isNext = slideIndex === originalIndex - 1
          // Flags to check for directional movement
          // Slides could obviously move anywhere but this is something
          // we all understand
          const movesRight = direction === 1
          const movesLeft = direction === -1

          // Clone the element to add the logic
          return cloneElement(child, {
            direction,
            isPrev,
            isNext,
            isCurrent,
            key: `slide_${originalIndex}`,
            fragmentOrder: isCurrent
              ? fragmentOrder
              : isPrev ? lastFragment : 0,
            slideIndex: originalIndex,
            fromPrev: isCurrent && movesLeft,
            fromNext: isCurrent && movesRight,
            toPrev: isPrev && movesRight,
            toNext: isNext && movesLeft
          })
        })
    )
  }

  /**
   * @private
   * @return {Wrapper}
   *   The entire Deck inside a Wrapper
   */
  render() {
    return (
      <Wrapper mixin={this.props.mixin}>
        {this.plugins}
        {this.elements}
        <SlidesWrapper>{this.visibleSlides}</SlidesWrapper>
      </Wrapper>
    )
  }
}

import {css} from 'styled-components'

/**
 * A collection of layouts for the speaker-deck
 * @private
 */
const layouts = [
  css`
    --view-scale: calc(1 / 6 * 4);
    --preview-scale: calc(1 / 6 * 2);
    --nextview-scale: calc(1 / 6 * 2);
    grid-template-rows: repeat(6, 1fr);
    grid-template-columns: repeat(6, 1fr);
    grid-template-areas:
      'Preview Preview Controls Controls Nextview Nextview'
      'Preview Preview Controls Controls Nextview Nextview'
      'View View View View Notes Notes'
      'View View View View Notes Notes'
      'View View View View Notes Notes'
      'View View View View Notes Notes';
  `,
  css`
    --view-scale: calc(1 / 3 * 2);
    --preview-scale: calc(1 / 3);
    --nextview-scale: calc(1 / 3);
    grid-template-rows: 1fr 1fr 1fr;
    grid-template-columns: 2fr 1fr;
    grid-template-areas:
      'View Preview'
