}

import React from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

/**
 * Styled icon wrapper
 * Adds basic color and sizing logic
 * @private
 */
export const StyledIcon = styled.span`
  font-size: ${({size}) => size || '1em'};

  svg,
  circle,
  rect,
  path,
  g {
    fill: currentColor;
  }
  svg {
    height: 1em;
    width: 1em;
  }
`

/**
 * Icon
 * Creates an SVG icon
 * @public
 * @param {Object} props
 *   The properties
 * @param {ReactElement} props.icon
 *   An SVG icon
 * @param {(ReactElement|ReactElement[])} props.children
 *   If children are provided the `icon` is ignored
 * @param {String} props.size
 *   size of the icon plus unit (e.g. 1rem)
 */
const Icon = ({children, icon, size = '1em'}) => (
  <StyledIcon size={size}>{children || icon}</StyledIcon>
)

/**
 * @private
 */
Icon.propTypes = {
  children: PropTypes.node,
  icon: PropTypes.node,
  size: PropTypes.string
}

/**
 * @private
 */
Icon.defaultProps = {
  children: null,
  icon: null,
  size: '1em'
}

export default Icon

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import Img from './img'
import Mask from './mask'

/**
 * Class for image.
 * It loads an image as a background-image. The original image is still
 * rendered for accessibility reasons.
 * @public
 * @class Image
 * @reactProps {Object} props
 *   The properties
 * @reactProps {String} props.alt
 * @reactProps {String} props.src
 * @reactProps {?String} props.title
 */
class Image extends Component {
  /**
   * @private
   */
  state = {}

  /**
   * @private
   * @return {{alt: String, src: String, title: ?String}}
   *   Allowed propTypes for `<Fragment/>`
   */
  static get propTypes() {
    return {
      alt: PropTypes.string.isRequired,
      src: PropTypes.string.isRequired,
      title: PropTypes.string
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      title: ''
    }
  }

  /**
   * Constructs the object.
   * @public
   * @param {Object} props
   *   The properties
   * @param {String} props.alt
   * @param {String} props.src
   * @param {?String} [props.title]
   */
  constructor(props) {
    super(props)
    this.getImage = this.getImage.bind(this)
    this.handleLoad = this.handleLoad.bind(this)
  }

  /**
   * Handles the load event of the image.
   * @private
   */
  handleLoad() {
    this.setState({
      height: this.image.height,
      width: this.image.width
    })
  }

  /**
   * Additional CSS rules for the mask
   * @private
   * @return {String|Array}
   */
  get mixin() {
    return `
      --height: ${this.state.height}px;
      --width: ${this.state.width}px;
      background-image: url("${this.props.src}");
    `
  }

  /**
   * Assigns the image to `ref`.
   * @private
   * @param {ReactElement} el
   */
  getImage(el) {
    /**
     * @private
     */
    this.image = el
  }

  /**
   * @private
   * @return {Mask}
   *   An image nested inside a mask
   */
  render() {
    return (
      <Mask mixin={this.mixin}>
        <Img
          innerRef={this.getImage}
          src={this.props.src}
          alt={this.props.alt}
          title={this.props.title}
          onLoad={this.handleLoad}
        />
      </Mask>
    )
  }
}

export default Image

import PropTypes from 'prop-types'
import styled from 'styled-components'
import Img from './img'

/**
 * The image mask
 * @private
 * @type {StyledComponent}
 * @param {Object} props
 *   The properties
 * @param {(Array|String)} props.mixin
 *  An additional mixin
 */
const Mask = styled.div`
  ${props => props.mixin || ''} background-size: cover;
  width: var(--width);
  height: var(--height);
`

/**
 * @private
 * @return {{mixin: ?(String|Array), children: (ReactElement|ReactElement[])}}
 *   Allowed propTypes for `<Mask/>`
 */
Mask.propTypes = {
  mixin: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
  children: PropTypes.instanceOf(Img)
}

export default Mask

