import React, {Component, Children, cloneElement} from 'react'
import PropTypes from 'prop-types'
import Slide from '@dekk/slide'
import Notes from '@dekk/speaker-notes'
import Fragment, {FragmentRoot} from '@dekk/fragment'
import Warning from './warning'
import {Slot, Static} from './components'

/**
 * Renders a component that displays a warning about an invalid
 * child component.
 * @private
 * @param {ReactComponent} child
 *   Can be any react component
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 * @returns {Warning}
 *   An invalid component warning
 */
const invalid = (child, index, itemProps) => (
  <div key={`slot__${index}`} data-slot={itemProps.name}>
    <Warning
      {...itemProps}
      type={
        (child.type && (child.type.displayName || child.type.name)) ||
        `"${child}"`
      }
      invalid
    />
  </div>
)

/**
 * Renders a component that displays a warning about an invalid
 * child component.
 * @private
 * @param {String} name
 *   Name of the Slot
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 * @returns {Warning}
 *   A missing component warning
 */
const missing = (name, index, itemProps) => (
  <div key={`slot__${index}`} data-slot={name}>
    <Warning {...itemProps} missing />
  </div>
)

/**
 * Either renders a component defined in the `only` parameter
 * or returns a warning in case the child does not match the accepted
 * types.
 * @private
 * @param {ReactComponent[]} only
 *   A list of ReactComponents that are allowed
 * @param {ReactComponent} child
 *   The child element that is intended to be added
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 */
const onlyOrWarning = (only, child, index, itemProps) => {
  // Check if child is allowed
  if (only.includes(child.type)) {
    return child
  }
  // In case the child is a fragment, check all child elements
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        onlyOrWarning(only, child)
      )
    })
  }
  return invalid(child, index, itemProps)
}

/**
 * Either renders a component not defined in the `not` parameter
 * or returns a warning in case the child matches the unaccepted
 * types.
 * @private
 * @param {ReactComponent[]} not
 *   A list of ReactComponents that are not allowed
 * @param {ReactComponent} child
 *   The child element that is intended to be added
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 */
const notOrWarning = (not, child, index, itemProps) => {
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        notOrWarning(not, child)
      )
    })
  }
  if (not.includes(child.type)) {
    return invalid(child, index, itemProps)
  }
  return child
}

/**
 * This component is hte heart of Dekk/master.
 * It will filter different content types and evaluate based on the
 * configuration.
 *
 * Invalid or missing slots are marked with a visual error to allow
 * creating slides on very strict guidelines.
 *
 * **A master can have different use cases:**
 *
 * * resrict layout of slides
 * * render static components by default
 * * predefine a layout/styles
 * * provide an easy API/usage
 *
 * This class should not be used. It is used inside `createMaster`
 * @see {createMaster}
 *
 * @private
 * @param {Object} props
 *   The properties
 * @param {ReactElement,ReactElement[]} props.children
 *   The slots injected by the slide creator (developer/user)
 * @param {ReactElement,ReactElement[]} props.content
 *   The children injected by the createMaster function (internal/private)
 */
class Master extends Component {
  /**
   * All `Slot` instances
   * @private
   */
  get slots() {
    return Children.toArray(this.props.children).filter(
      child => child.type === Slot
    )
  }

  /**
   * All `Static` instances
   * @private
   */
  get statics() {
    return Children.toArray(this.props.children).filter(
      child => child.type === Static
    )
  }

  /**
   * Helper slots are filtered from the children.
   * These slots are allowed and will be ignored.
   * state setters, notes etc should be added here
   * @private
   */
  get helperSlots() {
    return [Notes]
  }

  /**
   * Filtered children of the component.
   * Excludes helperSlots
   * @private
   */
  get content() {
    return Children.toArray(this.props.content).filter(
      child => !this.helperSlots.includes(child.type)
    )
  }

  /**
   * Filtered notes of the slide.
   * @private
   */
  get notes() {
    return Children.toArray(this.props.content).filter(
      child => child.type === Notes
    )
  }

  /**
   * Fill all static slots.
   * Adds a wrapping element with a data-attribute.
   */
  get filledStatics() {
    return this.statics.map((item, i) => (
      <div key={`static__${i}`} data-static={item.props.name}>
        {item.props.children}
      </div>
    ))
  }

  /**
   * Fill all slots.
   * Checks validity and injects a warning in case of an error
   * @private
   */
  get filledSlots() {
    return (
      this.slots
        .map((item, i) => {
          const {only, not, required, component, name} = item.props
          // Get the index of the component inside slot
          const index = this.content.map(child => child.type).indexOf(component)

          // If no component present either return null
          // or a warning if the slot requires a child.
          if (index < 0) {
            return required ? missing(name, i, item.props) : null
          }
          const children = Children.toArray(
            this.content[index].props.children
          ).map(
            (child, idx) =>
              // Check for `only` and `not` options
              // If neither is defined simply return the child
              only
                ? onlyOrWarning(only, child, idx, item.props)
                : not ? notOrWarning(not, child, idx, item.props) : child
          )
          // If no children exist in a required slot
          // render a warning about missing content
          if (required && children.length === 0) {
            return missing(name, i, item.props)
          }

          // Return the components wrapped in a slot.
          return (
            <div key={`item__${i}`} data-slot={item.props.name}>
              {children}
            </div>
          )
        })
        // Filter to only return valid content
        .filter(x => Boolean(x))
    )
  }

  /**
   * @private
   */
  render() {
    /**
     * Return a slide with the entire content.
     */
    return (
      <Slide {...this.props}>
        {this.filledStatics}
        {this.filledSlots}
        {this.notes}
      </Slide>
    )
  }
}

/**
 * Allowed propTypes for `<Master/>`
 * @private
 * @property {Slot,Static} children
 * @property {ReactElement,ReactElement[]} content
 */
Master.propTypes = {
  children: (props, propName) => {
    /**
     * An error might be caused and is therefore returned .
     */
    const error = new Error(
      `"Master" only accepts children of type "Slot" or "Static"`
    )
    const hasErrors = Boolean(
      Children.toArray(props[propName])
        .map(child => {
          if (![Static, Slot].includes(child.type)) {
            return error
          }
          return child
        })
        .filter(x => x === error).length
    )
    return hasErrors ? error : null
  },
  content: PropTypes.node
}

Master.defaultProps = {
  children: '',
  content: ''
}

export default Master

/* global window */
import {Component} from 'react'
import PropTypes from 'prop-types'

/**
 * This component does not render any content but adds paging via key
 * commands.
 *
 * If a slide has fragments this component will split the slide into
 * different steps.
 * @public
 */
class Paging extends Component {
  /**
   * @private
   */
  static get propTypes() {
    return {
      trigger: PropTypes.oneOf(['keyup', 'keydown']),
      toNextFragment: PropTypes.func,
      toPrevFragment: PropTypes.func,
      toNextSlide: PropTypes.func,
      toPrevSlide: PropTypes.func,
      slideCount: PropTypes.number,
      slideIndex: PropTypes.number,
      fragmentCount: PropTypes.number,
      fragmentIndex: PropTypes.number
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      trigger: 'keydown',
      toNextFragment: () => null,
      toPrevFragment: () => null,
      toNextSlide: () => null,
      toPrevSlide: () => null,
      slideCount: 0,
      slideIndex: 0,
      fragmentCount: 0,
      fragmentIndex: 0
    }
  }

  /**
   * @public
   * @param {Object} props
   *   The properties
   * @param {String} [props.trigger='keyup']
   *   The event that triggers paging
   * @param {number} props.slideCount
   *   (Injected via Dekk)
   * @param {number} props.slideIndex
   *   (Injected via Dekk)
   * @param {number} props.fragmentCount
   *   (Injected via Dekk)
   * @param {number} props.fragmentIndex
   *   (Injected via Dekk)
   * @param {number} props.fragmentOrder
   *   (Injected via Dekk)
   * @param {function} props.toFragment
   *   (Injected via Dekk)
   * @param {function} props.toSlide
   *   (Injected via Dekk)
   * @param {function} props.toNextFragment
   *   (Injected via Dekk)
   * @param {function} props.toPrevFragment
   *   (Injected via Dekk)
   * @param {function} props.toNextSlide
   *   (Injected via Dekk)
   * @param {function} props.toPrevSlide
   *   (Injected via Dekk)
   * @example
   * import Deck, {Plugins} from '@dekk/deck'
   * import Paging from '@dekk/paging'
   *
   * export default (
   *   <Deck>
   *     <Plugins>
   *       <Paging/>
   *     </Plugins>
   *   </Deck>
   * )
   */
  constructor(props) {
    super(props)
    this.goTo = this.goTo.bind(this)
  }

  /**
   * Listen to events before we mount the component
   * @private
   */
  componentWillMount() {
    window.addEventListener(this.props.trigger, this.goTo)
  }

  /**
   * Unlisten to events before we unmount the component
   * @private
   */
  componentWillUnmount() {
    window.removeEventListener(this.props.trigger, this.goTo)
  }

  /**
   * Method to navigate to fragments or slides.
   * Uses left and right arrow buttons to navigate
   * @private
   * @param  {Object} e
   *   The event
   * @param {number} e.which
   *   The keyCode that has been triggered by the event
   */
  goTo({which}) {
    const {
      slideCount,
      slideIndex,
      fragmentIndex,
      fragmentCount,
      toNextSlide,
      toPrevSlide,
      toNextFragment,
      toPrevFragment
    } = this.props

    const hasFragments = Boolean(fragmentCount)

    const lastFragment = Math.max(0, fragmentCount - 1)

    const previousFragment = Math.max(0, fragmentIndex - 1)
    const nextFragment = Math.min(lastFragment, fragmentIndex + 1)

    const lastSlide = Math.max(0, slideCount - 1)
    const previousSlide = Math.max(0, slideIndex - 1)
    const nextSlide = Math.min(lastSlide, slideIndex + 1)

    const handleNext = () => {
      if (hasFragments && nextFragment > fragmentIndex) {
        toNextFragment()
      } else if (nextSlide !== slideIndex) {
        toNextSlide()
      }
    }

    const handlePrev = () => {
      if (hasFragments && previousFragment < fragmentIndex) {
        toPrevFragment()
      } else if (previousSlide !== slideIndex) {
        toPrevSlide()
      }
    }

    // Switch between left and right arrow buttons
    switch (which) {
      case 39:
        handleNext()
        break
      case 37:
        handlePrev()
        break
      default:
        break
    }
  }

  /**
   * @private
   */
  render() {
    return null
  }
}

export default Paging

import React, {Component, Children} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import {Motion, spring, presets} from 'react-motion'
import {slide} from '@dekk/animation'
import Notes from '@dekk/speaker-notes'

/**
 * A single slide.
 * Renders a slide inside a `react-motion` wrapper.
 * Assigns css-variables to allow various transitions
 * @param {Object} props
 * @param {Boolean} props.isCurrent
 * @param {Boolean} props.isPrev
 * @param {Boolean} props.isNext
 * @param {Boolean} props.toPrev
 * @param {Boolean} props.fromPrev
 * @param {Boolean} props.toNext
 * @param {Boolean} props.fromNext
 * @param {Boolean} props.isPreview
 * @param {Boolean} props.present
 * @param {number} props.fragmentOrder
 * @param {String} props.className
 * @param {(ReactElement|ReactElement[])} props.children
 * @param {Object} props.springSettings
 * @param {number} props.springSettings.stiffness
 * @param {number} props.springSettings.damping
 * @param {number} props.springSettings.precision
 * @param {(Array|String)} props.animation
 * @param {(Array|String)} props.animationIn
 * @param {(Array|String)} props.animationOut
 * @param {(Array|String)} props.mixin
 * @param {String} props.background
 * @param {number} props.slideIndex
 */
class Slide extends Component {
  /**
   * @private
   */
  static get childContextTypes() {
    return {
      isPreview: PropTypes.bool,
      fragmentOrder: PropTypes.number,
      fragmentHost: PropTypes.number
    }
  }

  /**
   * @private
   */
  static get propTypes() {
    return {
      isCurrent: PropTypes.bool,
      isPrev: PropTypes.bool,
      isNext: PropTypes.bool,
      toPrev: PropTypes.bool,
      fromPrev: PropTypes.bool,
      toNext: PropTypes.bool,
      fromNext: PropTypes.bool,
      isPreview: PropTypes.bool,
      present: PropTypes.bool,
      fragmentOrder: PropTypes.number,
      className: PropTypes.string,
      children: PropTypes.node.isRequired,
      springSettings: PropTypes.shape({
        stiffness: PropTypes.number,
        damping: PropTypes.number,
        precision: PropTypes.number
      }),
      animation: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      animationIn: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      animationOut: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      mixin: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      background: PropTypes.string,
      slideIndex: PropTypes.number
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      isCurrent: false,
      isPrev: false,
      isNext: false,
      toPrev: false,
      fromPrev: false,
      toNext: false,
      fromNext: false,
      isPreview: false,
      present: false,
      fragmentOrder: 0,
      className: '',
      springSettings: presets.stiff,
      animation: '',
      animationIn: '',
      animationOut: '',
      mixin: '',
      background: '',
      slideIndex: -1
    }
  }

  /**
   * Get contextTypes
   * @private
   *
   * @return {{store: store, fragmentHost: number, hostedFragmentOrder: number}}
   */
  static get contextTypes() {
    return {
      store: PropTypes.object.isRequired
    }
  }

  /**
   * @private
   */
  getChildContext() {
    return {
      isPreview: this.props.isPreview,
      fragmentHost: this.props.slideIndex,
      fragmentOrder: this.props.fragmentOrder || 0
    }
  }

  /**
   * Helper slots are filtered from the children.
   * These slots are allowed and will be ignored.
   * state setters, notes etc should be added here
   * @private
   */
  get helperSlots() {
    return [Notes]
  }

  /**
   * Filtered children of the component.
   * Excludes helperSlots
   * @private
   */
  get content() {
    return Children.toArray(this.props.children).filter(
      child => !this.helperSlots.includes(child.type)
    )
  }

  /**
   * Filtered notes of the slide.
   * @private
   */
  get notes() {
    return this.getNotes(this.props.children)
  }

  /**
   * Get notes from Items
   * @private
   */
  getNotes(items) {
    return Children.toArray(items).filter(child => child.type === Notes)
  }

  /**
   * @private
   * @param {(ReactElement|ReactElement[])} notes
   * @param {number} slideIndex
   */
  setNotes(notes, slideIndex) {
    this.context.store.notes.splice(slideIndex, 1, notes)
  }

  /**
   * @private
   */
  componentWillMount() {
    if (this.props.isCurrent) {
      this.setNotes(this.notes, this.props.slideIndex)
    }
  }

  /**
   * @private
   */
  componentWillReceiveProps(newProps) {
    if (newProps.isCurrent && newProps.slideIndex !== this.props.slideIndex) {
      this.setNotes(this.getNotes(newProps.children), newProps.slideIndex)
    }
  }

  /**
   * @private
   */
  render() {
    const {isPrev, isNext, isCurrent} = this.props
    const direction = (() => {
      if (this.props.fromPrev) {
        return -1
      }
      if (this.props.fromNext) {
        return 1
      }
      if (this.props.toPrev) {
        return -1
      }
      if (this.props.toNext) {
        return 1
      }
      if (isPrev) {
        return -1
      }
      if (isNext) {
        return 1
      }
      if (isCurrent) {
        return 0
      }
      return -1
    })()

    // Switch spring direction
    const springStyle = {
      time: spring(isPrev || isNext ? 1 : 0, {
        ...this.props.springSettings
      })
    }
    return (
      <Motion style={springStyle}>
        {({time}) => {
          const style = {
            '--time': time
          }
          return (
            <StyledSlide
              className={this.props.className}
              style={style}
              direction={direction}
              present={this.props.present}
              background={this.props.background}
              mixin={this.props.mixin}
              animation={this.props.animation}
              animationIn={this.props.animationIn}
              animationOut={this.props.animationOut}>
              {this.content}
            </StyledSlide>
          )
        }}
      </Motion>
    )
  }
}

/**
 * @private
 */
const SlideDirection = styled.div`
  --direction: ${({direction}) => direction};
  z-index: ${({isCurrent}) => (isCurrent ? 1 : 0)};
`

/**
 * @private
 */
const switchAnimation = props => {
  if (props.direction === 1 && props.animationIn) {
    return props.animationIn
  }
  if (props.direction === -1 && props.animationOut) {
    return props.animationOut
  }
  return props.animation || slide.normal
}

/**
 * @private
 */
const StyledSlide = styled(SlideDirection)`
  position: absolute;
  top: 0;
  left: 0;
  overflow: hidden;
  color: var(--slide-color, currentColor);
  background: ${({background}) =>
    background || 'var(--slide-background, none)'};
  background-size: cover;
  ${({mixin}) => mixin || ''};
  ${props =>
    props.present
      ? `
      transform: scale3d(var(--scale), var(--scale), 1);
      transform-origin: 0 0;
      width: calc(100% / var(--scale));
      height: calc(100% / var(--scale));
      right: auto;
      bottom: auto;
    `
      : `
      right: 0;
      bottom: 0;
      ${switchAnimation(props)};
    `};
`

export default Slide

import {Children, cloneElement} from 'react'
import PropTypes from 'prop-types'

/**
