import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

/**
 * Styled version of the Fragment component
 * @see Fragment
 * @private
 * @type {StyledComponent}
 * @reactProps {Object} props
 *   The properties
 * @reactProps {(ReactNode|ReactNode[])} children
 * @reactProps {?Boolean} active
 * @reactProps {?string} animation
 */
const StyledFragment = styled.span`
  display: inline-block;
  white-space: pre-wrap;
  width: inherit;
  ${props =>
    props.animation ||
    `
      opacity: ${props.active ? 1 : 0}
  `};
`

/**
 * @private
 * @type {String}
 */
StyledFragment.displayName = 'StyledFragment'

/**
 * @private
 * @type {Object}
 * @prop {(ReactNode|ReactNode[])} children
 * @prop {?Boolean} active
 * @prop {?(String|Array)} animation
 */
StyledFragment.propTypes = {
  children: PropTypes.node,
  active: PropTypes.bool,
  animation: PropTypes.oneOfType([PropTypes.array, PropTypes.string])
}

export default StyledFragment

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

import StyledFragment from './fragment'

/**
 * Class for fragment.
 * @public
 *
 * @class Fragment
 * @param {Object} props
 *   The properties
 * @param {(ReactElement|ReactElement[])} props.children
 * @param {number} props.order
 * @param {?Boolean} props.root
 * @param {Object} context
 *   The context
 */
export default class Fragment extends Component {
  /**
   * @private
   * @return {{children: (ReactElement|ReactElement[]), animation: ?(String|Array), order: ?number}}
   *   Allowed propTypes for `<Fragment/>`
   */
  static get propTypes() {
    return {
      children: PropTypes.node.isRequired,
      animation: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
      order: PropTypes.number.isRequired
    }
  }

  /**
   * Get contextTypes
   * @private
   *
   * @return {{store: store, fragmentHost: number, hostedFragment: number}}
   */
  static get contextTypes() {
    return {
      store: PropTypes.object.isRequired,
      fragmentHost: PropTypes.number,
      hostedFragment: PropTypes.number
    }
  }

  /**
   * Sends context to decendants. Includes the app store to allow all deck
   * components to access the store without having to include its own logic.
   * @private
   *
   * @return {{hostedFragment: number}}
   */
  static get childContextTypes() {
    return {
      hostedFragment: PropTypes.number
    }
  }

  /**
   * Constructor
   * @public
   * @param {Object}                  props
   *   The properties
   * @param {(ReactNode|ReactNode[])} props.children
   * @param {number}                  props.order
   * @param {?Boolean}                props.root
   * @param {Object}                  context
   *   The context
   */
  constructor(props, context) {
    super(props, context)
  }

  /**
   * Gets the child context.
   * @private
   * @return {{hostedFragment: number}}
   *   The child context.
   */
  getChildContext() {
    return {
      hostedFragment: this.props.root ? this.props.order : 0
    }
  }

  /**
   * Before the fragments are mounted we need to tell the application about
   * their exitsance and behavior.
   * @private
   */
  componentWillMount() {
    const {order} = this.props
    const {fragmentHost, store, hostedFragment = 0} = this.context
    // Get the current host to allow extending it.
    const host = store.fragmentHosts[fragmentHost] || []

    // Handle root components. the root index is added to nested fragments
    // to allow them to be rendered before or after their parent fragment
    const fragmentOrder = order + hostedFragment

    // Only add new indexes to the queue.
    // Duplicate indexes will appear at the same time.
    // Root components are respected by definition.
    if (host.indexOf(fragmentOrder) < 0) {
      host.push(fragmentOrder)
    }

    // If no fragment has been defined with `0` we need to add a position to
    // our host
    if (host.length && host.indexOf(0) < 0) {
      host.push(0)
    }
    // Sort the fragment indexes and update the store
    store.fragmentHosts[fragmentHost] = host.sort((a, b) => a - b)
  }

  /**
   * @private
   * @return {number}
   *   The number of fragment indexes (steps to next slide)
   */
  get length() {
    return this.context.store.fragmentHosts[this.context.fragmentHost].length
  }

  /**
   * @private
   * @return {StyledFragment}
   *   The fragment including the entire logic
   */
  render() {
    const {order, children, animation} = this.props
    const {fragmentHost, store, hostedFragment = 0} = this.context
    // Define several flags to determine the acitve state
    // of the fragment.
    const isPrevious = fragmentHost < store.page
    const isNext = fragmentHost > store.page
    const fragmentOrder = order + hostedFragment
    const isZero = fragmentOrder === 0
    const isActivated = store.fragment >= fragmentOrder
    const isActive = isPrevious || (isNext ? isZero : isActivated)
    return (
      <StyledFragment active={isActive} animation={animation}>
        {children}
      </StyledFragment>
    )
  }
}

/**
 * Fragment root component. When using the fragmentRoot, nested Fragments start
 * after the parent. This is a curry version of Fragment.
 * @see    Fragment
 * @public
 * @class  FragmentRoot
 * @reactProps {Object} props
 * @reactProps {(ReactElement|ReactElement[])} props.children
 * @reactProps {number} props.order
 * @return {Fragment}
 *   A Fragment component as root
 */
export const FragmentRoot = props => <Fragment {...props} root />

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled, {css} from 'styled-components'

/**
 * Class for image.
 * It loads an image as a background-image. The original image is still
 * rendered for accessibility reasons.
 * @public
 * @class Image
 * @reactProps {Object} props
 *   The properties
 * @reactProps {String} props.alt
 * @reactProps {String} props.src
 * @reactProps {?String} props.title
 */
class Image extends Component {
  /**
   * @private
   */
  state = {}
  /**
   * @private
   * @return {{alt: String, src: String, title: ?String}}
   *   Allowed propTypes for `<Fragment/>`
   */
  static get propTypes() {
    return {
      alt: PropTypes.string.isRequired,
      src: PropTypes.string.isRequired,
      title: PropTypes.string
    }
  }
  /**
   * Constructs the object.
   * @public
   * @param {Object} props
   *   The properties
   * @param {String} props.alt
   * @param {String} props.src
   * @param {?String} props.title
   */
  constructor(props) {
    super(props)
    this.getImage = this.getImage.bind(this)
    this.handleLoad = this.handleLoad.bind(this)
  }

  /**
   * Handles the load event of the image.
   * @private
   */
  handleLoad() {
    this.setState({
      loaded: true,
      height: this.image.height,
      width: this.image.width
    })
  }

  /**
   * Additional CSS rules for the mask
   * @private
   * @return {String|Array}
   */
  get mixin() {
    return css`
      --height: ${this.state.height}px;
      --width: ${this.state.width}px;
      background-image: url("${this.props.src}");
    `
  }

  /**
   * Assigns the image to `ref`.
   * @private
   * @param {ReactElement} el
   */
  getImage(el) {
    /**
     * @private
     */
    this.image = el
  }

  /**
   * @private
   * @return {Mask}
   *   An image nested inside a mask
   */
  render() {
    return (
      <Mask mixin={this.mixin}>
        <Img
          ref={this.getImage}
          src={this.props.src}
          alt={this.props.alt}
          title={this.props.title}
          onLoad={this.handleLoad}
        />
      </Mask>
    )
  }
}

/**
 * The image mask
 * @private
 * @type {StyledComponent}
 */
const Mask = styled.div`
  ${props => props.mixin || ''} background-size: cover;
  width: var(--width);
  height: var(--height);
`

/**
 * @private
 * @return {{mixin: ?(String|Array), children: (ReactElement|ReactElement[])}}
 *   Allowed propTypes for `<Mask/>`
 */
Mask.propTypes = {
  mixin: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
  children: PropTypes.instanceOf(Img)
}

/**
 * Styled img
 * @private
 * @type {StyledComponent}
 */
const Img = styled.img`
  ${props => (props.loaded ? 'display: none;' : '')};
`

/**
 * @private
 * @return {{loaded: Boolean, onLoad: Function, ref: Function, src: String, alt: String, title ?String)}}
 *   Allowed propTypes for `<Img/>`
 */
Img.propTypes = {
  loaded: PropTypes.boolean,
  onLoad: PropTypes.func,
  ref: PropTypes.func,
  src: PropTypes.string.isRequired,
  alt: PropTypes.string.isRequired,
  title: PropTypes.string
}

export default Image

import React, {Component} from 'react'
import PropTypes from 'prop-types'

class Listener extends Component {
  /**
   * @private
   */
  static get propTypes() {
    return {
      onPage: PropTypes.func,
      onFragment: PropTypes.func
    }
  }

  /**
   * @private
   */
  static get defaultProps() {
    return {
      onPage: () => null,
      onFragment: () => null
    }
  }

  /**
   * @private
   * @param {Object} props
   *   The properties
   */
  constructor(props) {
    super(props)
  }

  /**
   * @private
   */
  componentWillReceiveProps({page, fragment, fragmentCount}) {
    if (
      this.props.fragmentCount !== fragmentCount &&
      typeof fragment !== 'undefined'
    ) {
      this.props.onFragment(page, fragmentCount, fragment)
    } else if (this.props.page !== page) {
      this.props.onPage(page)
    }
  }

  /**
   * @private
   */
  render() {
    return null
  }
}

export default Listener

import React, {Component, Children, cloneElement} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import Slide from '@dekk/slide'
import Notes from '@dekk/speaker-notes'
import Fragment, {FragmentRoot} from '@dekk/fragment'
import Warning from './warning'
import {Slot, Static} from './components'

/**
 * Renders a component that displays a warning about an invalid
 * child component.
 * @private
 * @param {ReactComponent} child
 *   Can be any react component
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 * @returns {Warning}
 *   An invalid component warning
 */
const invalid = (child, index, itemProps) => (
  <div key={`slot__${index}`} data-slot={name}>
    <Warning
      {...itemProps}
      type={
        (child.type && (child.type.displayName || child.type.name)) ||
        `"${child}"`
      }
      invalid
    />
  </div>
)

/**
 * Renders a component that displays a warning about an invalid
 * child component.
 * @private
 * @param {String} name
 *   Name of the Component
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 * @returns {Warning}
 *   A missing component warning
 */
const missing = (name, index, itemProps) => (
  <div key={`slot__${index}`} data-slot={name}>
    <Warning {...itemProps} missing />
  </div>
)

/**
 * Either renders a component defined in the `only` parameter
 * or returns a warning in case the child does not match the accepted
 * types.
 * @private
 * @param {ReactComponent[]} only
 *   A list of ReactComponents that are allowed
 * @param {ReactComponent} child
 *   The child element that is intended to be added
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 */
const onlyOrWarning = (only, child, index, itemProps) => {
  // Check if child is allowed
  if (only.includes(child.type)) {
    return child
  }
  // in case the child is a fragment, check all child elements
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        onlyOrWarning(only, child)
      )
    })
  }
  return invalid(child, index, itemProps)
}

/**
 * Either renders a component not defined in the `not` parameter
 * or returns a warning in case the child matches the unaccepted
 * types.
 * @private
 * @param {ReactComponent[]} not
 *   A list of ReactComponents that are not allowed
 * @param {ReactComponent} child
 *   The child element that is intended to be added
 * @param {number} index
 *   Used to generate the key property
 * @param {Object} itemProps
 *   The original props of the parent
 */
const notOrWarning = (not, child, index, itemProps) => {
  if (child.type === Fragment || child.type === FragmentRoot) {
    return cloneElement(child, {
      children: Children.toArray(child.props.children).map(child =>
        notOrWarning(only, child)
      )
    })
  }
  if (not.includes(child.type)) {
    return invalid(child, index, itemProps)
  }
  return child
}

/**
 * This component is hte heart of Dekk/master.
 * It will filter different content types and evaluate based on the
 * configuration.
 *
 * Invalid or missing slots are marked with a visual error to allow
 * creating slides on very strict guidelines.
 *
 * **A master can have different use cases:**
 *
 * * resrict layout of slides
 * * render static components by default
 * * predefine a layout/styles
 * * provide an easy API/usage
 *
 * This class should not be used. It is used inside `createMaster`
 * @see {createMaster}
 *
 * @private
 * @param {Object} props
 *   The properties
 * @param {ReactElement,ReactElement[]} props.children
 *   The slots injected by the slide creator (developer/user)
 * @param {ReactElement,ReactElement[]} props.content
 *   The children injected by the createMaster function (internal/private)
 */
class Master extends Component {
  render() {
    const {children, content} = this.props
    // All `Slot` instances
    const slots = Children.toArray(children).filter(
      child => child.type === Slot
    )

    // All `Static` instances
    const statics = Children.toArray(children).filter(
      child => child.type === Static
    )

    // Helper slots are filtered from the children.
    // These slots are allowed and will be ignored.
    // state setters, notes etc should be added here
    const helperSlots = [Notes]

    // Filtered children of the component.
    // Excludes helperSlots
    const onlyContent = Children.toArray(content).filter(
      child => !helperSlots.includes(child.type)
    )

    // Fill all static stols.
    // Adds a wrapping element with a data-attribute.
    const filledStatics = statics.map((item, i) => (
      <div key={`static__${i}`} data-static={item.props.name}>
        {item.props.children}
      </div>
    ))

    const filledSlots = slots
      .map((item, i) => {
        const {only, not, required, component, name} = item.props
        // Get the index of the component inside slot
        const index = onlyContent.map(child => child.type).indexOf(component)
        /**
         * @deprecated
         * @todo Is this still needed?
         * Please describe this and add an example.
         */
        //const Element = item.props.as || 'div'

        // if no component present either return null
        // or a warning if the slot requires a child.
        if (index < 0) {
          return required ? missing(name, i, item.props) : null
        }
        const children = Children.toArray(
          onlyContent[index].props.children
        ).map(
          (child, idx) =>
            // Check for `only` and `not` options
            // If neither is defined simply return the child
            only
              ? onlyOrWarning(only, child, idx, item.props)
              : not ? notOrWarning(only, child, idx, item.props) : child
        )
        // If no children exist in a required slot
        // render a warning about missing content
        if (required && children.length === 0) {
          return missing(name, i, item.props)
        }

        // Return the components wrapped in a slot.
        return (
          <div key={`item__${i}`} data-slot={item.props.name}>
            {children}
          </div>
        )
      })
      // filter to only return valid content
      .filter(x => Boolean(x))

    /**
     * Return a slide with the entire content.
     * @todo add notes to allow rendering them in hte presenter view
     */
    return (
      <Slide {...this.props}>
        {filledStatics}
        {filledSlots}
      </Slide>
    )
  }
}

/**
 * Allowed propTypes for `<Master/>`
 * @private
 * @property {Slot,Static} children
 * @property {ReactElement,ReactElement[]} content
 */
Master.propTypes = {
  children: (props, propName, componentName) => {
    /**
     * An error might be caused and is therefore returned .
     * @todo find a more functional way of doing this.
     */
    let error
    const prop = props[propName]
    Children.forEach(prop, child => {
      if ([Static, Slot].indexOf(child.type) < 0) {
        error = new Error(
          `"Master" only accepts children of type "Slot" or "Static"`
        )
      }
    })
    return error
  },
  content: PropTypes.node
}

export default Master

import {Component} from 'react'
import PropTypes from 'prop-types'

/**
 * This component does not render any content but adds paging via key
 * commands.
 *
 * If a page has fragments this component will split the page into
 * different steps.
 * Without this components fragments won't work. They are rendered as
 * normal elements but never get activated.
 *
 * This is a private component which is used by Dekk internally.
 *
 * Due to the importance of this component it is not possible do
 * modify or access any parts. If you need acces to data you can always
 * use the store provided by Dekk.
 * @private
 */
class Paging extends Component {
  /**
   * @private
   */
  static get propTypes() {
    return {
      trigger: PropTypes.oneOf(['keyup', 'keydown']).isRequired,
      pages: PropTypes.number.isRequired
    }
  }

  /**
   * @private
   */
  static get contextTypes() {
    return {
      store: PropTypes.object.isRequired
    }
  }

  /**
   * @private
   * @param {Object} props
   *   The properties
   * @param {String} props.trigger
   *   The event that triggers paging
   * @param {number} props.pages
   * @param {Object} context
   *   The context
   * @param {Object} context.store
   *   The mobx store passed through via context
   */
  constructor(props, context) {
    super(props, context)
    this.goTo = this.goTo.bind(this)
  }

  /**
   * Listen to events before we mount the component
   * @private
   */
  componentWillMount() {
    window.addEventListener(this.props.trigger, this.goTo)
  }

  /**
   * Unlisten to events before we unmount the component
   * @private
   */
  componentWillUnmount() {
    window.removeEventListener(this.props.trigger, this.goTo)
  }

  /**
   * Method to navigate to fragments or pages.
   * Uses left and right arrow buttons to navigate
   * @private
   * @param  {Object} e
   *   The event
   * @param {number} e.which
   *   The keyCode that has been triggered by the event
   */
  goTo({which}) {
    const {pages} = this.props
    const {store} = this.context
    const {
      page,
      toPreviousPage,
      toNextPage,
      fragmentCount,
      fragmentHosts,
      toNextFragment,
      toPreviousFragment,
      setFragment
    } = store
    const {length = 0} = fragmentHosts[page]
    const previousFragment = Math.max(0, fragmentCount - 1)
    const nextFragment = Math.min(length - 1, fragmentCount + 1)
    const previousPage = Math.max(0, page - 1)
    const nextPage = Math.min(pages - 1, page + 1)

    // Switch between left and right arrow buttons
    switch (which) {
      case 39:
        ;(() => {
          if (length && nextFragment > fragmentCount) {
            toNextFragment()
          } else if (nextPage !== page) {
            toNextPage()
          }
        })()
        break
      case 37:
        ;(() => {
          if (length && previousFragment < fragmentCount) {
            toPreviousFragment()
          } else if (previousPage !== page) {
            toPreviousPage()
          }
        })()
        break
      default:
        break
    }
  }

  /**
   * @private
   */
  render() {
    return null
  }
}

export default Paging

import React, {Component} from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import {Motion, spring} from 'react-motion'

/**
 * A single slide.
 * Renders a slide inside a `react-motion` wrapper.
 * Assigns css-variables to allow various transitions
 *
 */
class Slide extends Component {
  /**
   * @private
   */
  static get childContextTypes() {
    return {
      fragmentHost: PropTypes.number
    }
  }

  /**
   * @private
   */
  static get propTypes() {
    return {
      current: PropTypes.bool,
      previous: PropTypes.bool,
      next: PropTypes.bool,
      toPrevious: PropTypes.bool,
      fromPrevious: PropTypes.bool,
      toNext: PropTypes.bool,
      fromNext: PropTypes.bool,
      className: PropTypes.string,
      children: PropTypes.node,
      springSettings: PropTypes.object,
      background: PropTypes.string
    }
  }

  /**
   * @private
   */
  getChildContext() {
    return {
      fragmentHost: this.props.pageIndex
    }
  }

  /**
   * @private
   */
  render() {
    const {previous, next, children, springSettings} = this.props
    const springStyle = {
      time: spring(previous || next ? 1 : 0, {
        ...springSettings
      })
    }
    return (
      <Motion style={springStyle}>
        {({time}) => {
          const style = {
            '--time': ~~(time * 1000) / 1000
          }
          return (
            <StyledSlide
              background={this.props.background}
              mixin={this.props.animation}
              className={this.props.className}
              current={this.props.current}
              next={next}
              previous={previous}
              toPrevious={this.props.toPrevious}
              toNext={this.props.toNext}
              fromPrevious={this.props.fromPrevious}
              fromNext={this.props.fromNext}
              style={style}>
              {children}
            </StyledSlide>
          )
        }}
      </Motion>
    )
  }
}

/**
 * @private
 */
const SlideDirection = styled.div`
  ${props => {
    if (props.fromPrevious) {
      return `
        --direction: -1;
      `
    }
    if (props.fromNext) {
      return `
        --direction: 1;
      `
    }

    if (props.toPrevious) {
      return `
        --direction: -1;
      `
    }
    if (props.toNext) {
      return `
        --direction: 1;
      `
    }

    if (props.previous) {
      return `
        --direction: -1;
      `
    }
    if (props.next) {
      return `
        --direction: 1;
      `
    }
    if (props.current) {
      return `
        --direction: 0;
      `
    }
  }} z-index: ${props => (props.current ? 1 : 0)};
`

/**
 * @private
 */
const StyledSlide = styled(SlideDirection)`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  transform: translate3d(
    calc(100% * var(--direction, -1) * var(--time, 1)),
    0,
    0
  );
  overflow: hidden;
  color: var(--slide-color, inherit);
  background: ${props => props.background || 'none'};
  background-size: cover;
  ${props => props.mixin || ''};
`

export default Slide

import uuid from 'uuid/v4'
import {observable} from 'mobx'

/**
 * @private
 */
export default class Store {
  id = uuid()
  @observable page = 0
  @observable fragment = 0
  @observable fragmentCount = 0
  @observable direction = 0
  constructor(props) {
    this.page = props.page
    this.fragmentHosts = props.fragmentHosts
    this.title = props.title

    this.goToPage = this.goToPage.bind(this)
    this.toNextPage = this.toNextPage.bind(this)
    this.toPreviousPage = this.toPreviousPage.bind(this)
    this.goToFragment = this.goToFragment.bind(this)
    this.toNextFragment = this.toNextFragment.bind(this)
    this.toPreviousFragment = this.toPreviousFragment.bind(this)
  }

  /**
   * @private
   */
  goToPage(n) {
    this.direction = n > this.page ? 1 : -1
    this.page = n
  }

  /**
   * @private
   */
  toNextPage() {
    this.goToPage(this.page + 1)
    this.goToFragment(0)
  }

  /**
   * @private
   */
  toPreviousPage() {
    const {length} = this.fragmentHosts[this.page - 1]
    this.goToPage(this.page - 1)
    this.goToFragment(Math.max(0, length - 1))
  }

  /**
   * @private
   */
  goToFragment(n) {
    const m = this.fragmentHosts[this.page][n]
    this.fragment = m
    this.fragmentCount = n
  }

  /**
   * @private
   */
  toNextFragment() {
    this.goToFragment(this.fragmentCount + 1)
  }

  /**
   * @private
   */
  toPreviousFragment() {
    this.goToFragment(this.fragmentCount - 1)
  }
}

